import styled from 'styled-components';
export const ShikhNames = styled.div`
    background-color: ${props => props.themeStyle[props.theme].secondaryColor};
    padding: 9px 15px;
    border-radius: 7px;
    text-align: start;
    font-size: 15px;
    box-shadow: ${props => props.themeStyle[props.theme].boxShadow};
    .text_name h6 {
        text-transform: capitalize;
        text-decoration: none;
        color: ${props => props.themeStyle.all.mainColor};
        transition: all 0.3s ease-in-out;
        font-weight: normal;
        cursor: pointer;
        &:hover {
            color: ${props => props.themeStyle.all.secondaryColor};
        }
    }
    .info {
        text-align: start;
        .suras_count {
            color: ${props => props.themeStyle.all.yellowColor};
            font-size: 18px;
            font-weight: bold;
            flex-basis: 30%;
        }
        .count {
            background-color: ${props => props.themeStyle.all.mainColor};
            border-radius: 50%;
            padding: 10px;
            font-weight: bold;
            color: ${props => props.themeStyle.all.whiteColor};
        }
    }
`;
