import styled, { keyframes } from 'styled-components';

const showUp = keyframes`
    0% {
        transform: scale(0);
        left: -120vw;
        opacity: 0;
    }
    100% {
        transform: scale(1);
        left: 20px;
        opacity: 1;
    }
`;

export const ToastCompoStyled = styled.div`
    position: fixed;
    bottom: 20px;
    text-align: start;
    ${props => props.site_lang === 'eng' ? 'left' : 'right'}: 20px;
    box-shadow: 0 2px 8px 0 hsl(0deg 0% 64% / 19%);
    background-color: #FFF;
    padding: 10px 30px;
    animation: ${showUp} 1s ease-in-out alternate;
    color: #1d1f21;
    border-radius: 6px;
    border-top: 3px solid ${props => props.myClass === 'added_item' ? '#39C2C9' : '#F5B44B'}
`;
