import styled from 'styled-components';

export const Aside = styled.div`
    position: fixed;
    width: 210px;
    border-radius: 6px;
    background-color: #FFF;
    padding: 20px 0px;
    box-shadow: 0 2px 8px 0 hsl(0deg 0% 64% / 19%);
    & > aside {
        color: ${props => props.theme === 'light' ? '#080' : '#f60'}
    }
`;
