import React, { useState, useEffect } from 'react';
import { FavouriteStyled } from './favouriteStyled';
import Form from 'react-bootstrap/Form';
import FavoriteList from './favoriteList';
import { connect } from 'react-redux';
import * as actions from '../../store/action/getData';
import { CgTrashEmpty } from 'react-icons/cg';


const Favorite = props => {

    const [search, setSearch] = useState('');
    const [playlistFavorite, setPlaylistFavorite] = useState(props.playlist);
    const [allElements, setAllElements] = useState([]);

    useEffect(() => {
        props.emptySuras();
        setPlaylistFavorite(props.playlist);
    }, [props.playlist]);

    let favArray = [];
    useEffect(() => {
        // console.log('ZXCVB');
        playlistFavorite.map(favorite => {
            let favArray = [];
            // console.log(favorite);
            if(search === '') {
                console.log('QQQ');
                // favArray.push(
                //     <FavoriteList
                //         key={`${favorite.server}/${favorite.name}/${favorite.suraName}`}
                //         name={favorite.name}
                //         number={favorite.number}
                //         server={favorite.server}
                //         suraName={favorite.suraName}
                //         rewaya={favorite.rewaya}
                //         deleteItems={(item) => deleteItems(item)}
                //     />
                // );

                // favArray.push(props.playlist)
                favArray.push(playlistFavorite);

            } else if(favorite.suraName.toLocaleLowerCase().includes(search.toLocaleLowerCase())) {
                // console.log('WWW');
                // console.log(favorite.suraName);

                // favArray.push(
                //     <FavoriteList
                //         key={`${favorite.server}/${favorite.name}/${favorite.suraName}/${favorite.name}`}
                //         name={favorite.name}
                //         number={favorite.number}
                //         server={favorite.server}
                //         suraName={favorite.suraName}
                //         rewaya={favorite.rewaya}
                //         deleteItems={(item) => deleteItems(item)}
                //     />
                // );

                favArray.push(favorite)
            }
        });
        setAllElements(favArray);
        // setPlaylistFavorite();
    }, [search]);

    const deleteItems = (item) => {
        props.deleteFavFromSurabox(item.server);
        props.deleteItemFromPlaylist(item);
    }

    // console.log(playlistFavorite);
    console.log('Favorite');

    return (
        <FavouriteStyled theme={props.theme} themeStyle={props.themeStyle}>
            <h2 className='title text-capitalize mb-4'>{props.site_language && props.site_language.favorite}</h2>
            <div className='form mb-4'>
                <Form>
                    <Form.Control type='text' placeholder='search by reciter name...' className='rounded-pill search-filed' onChange={e => setSearch(e.target.value)} dir='auto' />
                </Form>
            </div>
            <ul className='playlist-content'>
            {/* {console.log(playlistFavorite)} */}
                {/* {playlistFavorite} */}
                {/* {favArray} */}
                
                {
                    allElements.length > 0 ? (
                        allElements.map(favorite =>
                            // console.log(favorite)
                            <FavoriteList
                                key={`${favorite.server}/${favorite.name}/${favorite.suraName}/${favorite.server}`}
                                name={favorite.name}
                                number={favorite.number}
                                server={favorite.server}
                                suraName={favorite.suraName}
                                rewaya={favorite.rewaya}
                                deleteItems={(item) => deleteItems(item)}
                            />
                        )
                    ) : <div className='no-list text-center'>{`${props.site_language && props.site_language.no_playlist_added}... `}<CgTrashEmpty size={28} /></div>
                }
                
                
                
                {/* {
                    playlistFavorite.length > 0 ? (
                        playlistFavorite.map(favorite =>
                            // console.log(favorite)
                            <FavoriteList
                                key={`${favorite.server}/${favorite.name}/${favorite.suraName}/${favorite.server}`}
                                name={favorite.name}
                                number={favorite.number}
                                server={favorite.server}
                                suraName={favorite.suraName}
                                rewaya={favorite.rewaya}
                                deleteItems={(item) => deleteItems(item)}
                            />
                        )
                    ) : <div className='no-list text-center'>{`${props.site_language && props.site_language.no_playlist_added}... `}<CgTrashEmpty size={28} /></div>
                } */}
            </ul>
        </FavouriteStyled>
    );
}

const mapStateToProps = state => ({
    playlist: state.playlist,
    theme: state.theme,
    themeStyle: state.themeStyle,
    site_language: state.site_language
});

const mapDispatchToProps = dispatch => ({
    emptySuras: () => dispatch(actions.emptySuras()),
    deleteItemFromPlaylist: (item) => dispatch(actions.removeItemFromPlayList(item)),
    deleteFavFromSurabox: (item) => dispatch(actions.deleteFavFromSurabox(item))
});

export default connect(mapStateToProps, mapDispatchToProps)(Favorite);
