import React, { useEffect, useState, useCallback } from 'react';
import './layout.css';
import Navigation from '../Navagations/Navigation';
import Asdie from '../aside/aside';
import Main from '../MainContent/main';
import HeaderTitle from '../headerTitle/headerTitle';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import PlayerSong from '../songPlayer/songPlayer';
import { connect } from 'react-redux';
import Sidebar from '../Navagations/sidebar/sidebar';



const Layout = props => {
    const [scrollTopScreen, setScrollTopScreen] = useState(0);
    useEffect(() => {
        window.onscroll = useCallback(() => {
            setScrollTopScreen(window.pageYOffset);
            console.log(window.pageYOffset)
        }, []);
    }, []);
    return (
        <>
            <Navigation />
            <HeaderTitle />
            <Sidebar />
            <main>
                <Container>
                    <Row>
                        <Col md={4} lg={3} className='d-none d-md-block'>
                            <Asdie />
                        </Col>
                        <Col md={8} lg={9}>
                            {props.children}
                        </Col>
                    </Row>
                </Container>
                { props.isShow && <PlayerSong /> }
            </main>
        </>
    )
};

