import styled from 'styled-components';

export const PlayListStyled = styled.li`
    height: 45px;
    display: flex;
    align-items: center;
    padding: 0 5px;
    .play_btn {
        flex-basis: 8%;
        height: 36px;
        border-radius: 50%;
        background-color: #f5b44b;
        box-shadow: 0 0 6px 0 rgb(245 180 75 / 53%);
        .play-btn {
            margin-bottom: 1px;
            margin-left: 4px;
            svg {
                color: ${props => props.themeStyle.all.whiteColor} !important;
            }
        }
    }
    .info {
        padding: 0 10px;
        flex-basis: 92%;
        color: #02070e;
        .sura_number {
            font-size: 14px;
            flex-basis: 10%;
            color: ${props => props.theme === 'light' ? props.themeStyle.all.mutedColor : props.themeStyle.all.secondaryColor};
        }
        .sura_desc {
            display: flex;
            flex-basis: 92%;
            flex-direction: column;
            .sura_Name {
                font-weight: normal;
                color: ${props => props.themeStyle[props.theme].textNormal};
            }
            .sura_info {
                color: ${props => props.theme === 'light' ? props.themeStyle.all.mutedColor : props.themeStyle.all.yellowColor};
                font-size: 13px;
                width: 95%;
            }
            .sura_delete {
                flex-basis: 7%;
                color: ${props => props.themeStyle.all.redColor};
            }
        }
    }
`;
