import React, { useEffect, useState } from 'react';
import Letter from '../letters/letters';
import withErrorHandler from '../../hoc/withErrorHandler/withErrorHandler';
import axios from 'axios';
import ImamBoxs from '../imamBoxs/imamBoxs';
import './main.css';
import * as actions from '../../store/action/getData';
import { connect } from 'react-redux';
import Loader from '../UI/Loader/loader';
import Form from 'react-bootstrap/Form';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';

const Main = props => {
    const [allElements, setAllElements] = useState([]);
    const [search, setSearch] = useState('');
    const [rewayas, setRewayas] = useState([]);
    const [chooseSelect, setChooseSelect] = useState('select rewaya');
    const [allSections, setAllSections] = useState([]);

    useEffect(() => {
        let isMounted = true;
        const source = axios.CancelToken.source();
        
        const myAPI = async () => {
            props.startMain();
            props.emptyFavFromSurabox();
            await axios.get('http://mp3quran.net/api/_english.json', {
                cancelToken: source.token
            })
            .then(res => {
                if(isMounted) {
                    const arrayOfLetters = [];
                    const allElementsArrays = [];
                    const arrayOfLettersIndex = [];
                    let allRewayas = [];
                    const allArraysElements = res.data.reciters;
                    const myData = allArraysElements.map(letter => letter.letter);
                    myData.filter((element, index) => element !== myData[index + 1] && arrayOfLetters.push(element));
                    myData.filter((element, index) => element !== myData[index + 1] && arrayOfLettersIndex.push(index));
                    allArraysElements.map(element => allElementsArrays.push(element));
                    setAllElements(allArraysElements);
                    allArraysElements.map(e => {
                        if(!allRewayas.find(z => z === e.rewaya)) {
                            allRewayas.push(e.rewaya);
                        }
                    });

                    setRewayas(allRewayas);
                    // console.log(rewayas);
                    props.successMain(arrayOfLetters, arrayOfLettersIndex, allElementsArrays);
                }
            })
            .catch(error => {
                if(isMounted) {
                    return props.failRequest(error)
                }
            });
        };
        myAPI();

        return () => {
            isMounted = false;
            source.cancel();
        }
    }, []);

    
    let curentIndex = 0;
    useEffect(() => {
        console.log('JKL');
        let fullElementSections = [];
        if(props.letters && props.allElementsImams && props.lettersIndex) {
            console.log('M<>');
            for(let i = 0; i < props.letters.length; i++) {
                let speElement = props.allElementsImams.slice(curentIndex , props.lettersIndex[i] + 1);
                curentIndex= props.lettersIndex[i] + 1;
                fullElementSections.push({
                    letterSection: props.letters[i],
                    sectionElements: speElement
                });
            }
        }
        setAllSections(fullElementSections);
    }, [props.letters, props.allElementsImams, props.lettersIndex]);

    useEffect(() => {
        
        console.log(chooseSelect);



    }, [chooseSelect]);

    let show = <Loader />;

    if(!props.loading) {
        show = (
            <div className='show-names-letter'>
                <div className='main-content-show'>
                    {props.letters && props.letters.map(letter => <Letter text={letter} id={`#letter_${letter}`} key={letter} />) }
                </div>
                <div className='search_select mt-4 mb-4'>
                    <Row>
                        <Col lg='6'>
                            <div className='form-search mb-3 mb-lg-0'>
                                <Form>
                                    <Form.Control type='text' placeholder='Search by Sura reciter name...' className='rounded-pill search-filed' onChange={e => setSearch(e.target.value)} dir='auto' />
                                </Form>
                            </div>
                        </Col>
                        <Col lg='6'>
                            <div className='form-select'>
                                <Form>
                                    <Form.Group controlId="exampleForm.SelectCustom">
                                        <Form.Control as="select" custom className='select' value={chooseSelect} onChange={e => setChooseSelect(e.target.value)}>
                                        <option value='select rewaya' disabled='disabled'>select rewaya</option>
                                        <option value='all'>all</option>
                                        {rewayas.map(option => <option key={option} value={option}>{option}</option>)}
                                        </Form.Control>
                                    </Form.Group>
                                </Form>
                            </div>
                        </Col>
                    </Row>
                </div>
                <div className='show-all-names'>
                    {/* {console.log(fullElementSections)} */}
                    {
                        allElements && allElements.length > 0 && allSections.map((element, index) => {
                            return (
                                <ImamBoxs
                                    key={element.letterSection + element.letterSection}
                                    letter={element.letterSection}
                                    boxes={element.sectionElements}
                                    search={search}
                                    chooseSelect={chooseSelect}
                                    myIndex={index}
                                />
                            )
                        })
                    }
                </div>
            </div>
        )
    }
    return show;
};

const mapStateToProps = state => ({
    loading: state.loading,
    letters: state.letters,
    lettersIndex: state.lettersIndex,
    allElementsImams: state.allElementsImams
});

const mapDispatchToProps = dispatch => ({
    startMain: () => dispatch(actions.startMain()),
    successMain: (letters, lettersIndex, allEllemnt) => dispatch(actions.successMain(letters, lettersIndex, allEllemnt)),
    failRequest: () => dispatch(actions.failRequest()),
    emptyFavFromSurabox: () => dispatch(actions.emptyFavFromSurabox())
})

export default connect(mapStateToProps, mapDispatchToProps)(withErrorHandler(Main, axios));
