import styled from 'styled-components';

export const FavouriteStyled = styled.div`

    .title {
        font-size: 30px;
        color: #39c2c9;
    }
    .playlist-content {
        display: grid;
        grid-template-columns: repeat(auto-fill, minmax(300px, 1fr));
        grid-gap: 10px;
        flex-wrap: wrap;
        @media (max-width: 350px) {
            grid-template-columns: repeat(auto-fill, minmax(100%, 1fr));
        }
        .surah-box-imam-page {
            padding-top: 7px;
            padding-bottom: 7px;
            border-radius: 6px;
            background-color: #fff;
            box-shadow: 0 2px 8px 0 hsl(0deg 0% 64% / 19%);
            display: flex;
            justify-content: space-between;
            flex-wrap: wrap;
            .icon_name_number_section {
                flex-basis: 65%;
                @media (max-width: 350px) {
                    flex-basis: 100%;
                }
                .surah-box-number {
                    margin-left: 6px;
                    margin-right: 6px;
                }
                .surah-box-name {
                    color: #1d1f21;
                    white-space: nowrap;
                }
                .immam_name {
                    font-size: 13px;
                    letter-spacing: 0.3px;
                    padding: 0 6px;
                }
            }
            .surah-box-imam-page-info {
                flex-basis: 30%;
                justify-content: space-between;
                @media (max-width: 350px) {
                    flex-basis: 100%;
                    justify-content: flex-end;
                }
                .list {
                    cursor: pointer;
                    border-radius: 6px;
                    transition: all 0.3s ease-in-out;
                    &.hide_on_hover {
                        opacity: 0;
                        &:hover button {
                            color: #fff;
                        }
                    }
                    &.show_on_hover {
                        opacity: 1;
                    }
                    button {
                        color: #1d1f21;
                        padding: 2px 5px;
                    }
                    &.heart {
                        color: #1d1f21;
                        &.full_heart_btn {
                            color: #f5b44b;
                        }
                    }
                }
            }
            &:hover .surah-box-imam-page-info .list.hide_on_hover {
                opacity: 1;
            }
            &:hover .surah-box-imam-page-info .list.hide_on_hover:hover {
                background-color: #14eeaa;
            }
            &:hover .surah-box-imam-page-info .list.hide_on_hover:hover
                svg {
                    color: #fff;
                }
            }
        }
    }
`;
