import React from 'react';
import { BiPlay } from 'react-icons/bi';
import './playBtn.css';

const PlayBox = React.memo(props => {

    return (
        <div className='play-btn' title='Play icon' onClick={props.clicked}>
            <BiPlay size={props.size} color={props.color} />
        </div>
    )
});

export default PlayBox;
