import * as actionType from '../action/actionTypes';

const initialState = {
    // Get Data
    letters: null,
    lettersIndex: null,
    allElementsImams: null,
    getData: null,
    suras: [],
    server: null,
    showPlayer: false,
    suraLink: null,
    //  Status
    loading: false,
    isAsideShow: false,
    isRandom: false,
    // Favorite
    playlist: [],
    deleteFav: '',
    isFavPlaylist: false,
    isFavoritePage: false,
    isRadioPage: false,
    // Theme
    isThemeDark: false,
    theme: 'light',
    themeStyle: {
        all: {
            mainColor: '#39c2c9',
            secondaryColor: '#fa7e29',
            whiteColor: '#FFF',
            grayColor: '#636a6f',
            blackColor: '#1d1f21',
            redColor: '#dc3545',
            yellowColor: '#f5b44b',
            mutedColor: '#6c757d'
        },
        light: {
            bg: '#ebedee',
            secondaryColor: '#fff',
            textNormal: '#1d1f21',
            boxShadow: '0 2px 8px 0 hsl(0deg 0% 64% / 19%)'
        },
        dark: {
            bg: '#282c35',
            secondaryColor: '#363c48',
            textNormal: '#fff',
            boxShadow: '0 2px 15px 0 rgb(26 26 27 / 64%)'
        }
    },
    //  Language
    language: {lang: 'en', language: '_english.json'},
    site_language: null
}

const myReducer = (state = initialState, action) => {
    switch(action.type) {
        
        //  Setting
        case actionType.CLOSE_ASIDE:
            return {...state, isAsideShow: false};
        case actionType.OPEN_ASIDE:
            return {...state, isAsideShow: true};
        case actionType.iS_RADIO_PAGE:
            return {...state, isRadioPage: action.value}
        
        //  Status Request
        case actionType.START_MAIN:
            return {...state, loading: true,isRandom: false};
            // return {...state, loading: true, letters: null, lettersIndex: null, allElementsImams: null, server: null, suras: [], isRandom: false};
        case actionType.SUCCESS_MAIN:
            return {...state, loading: false, letters: action.letters, lettersIndex: action.letterIndexs, allElementsImams: action.allElements};
        case actionType.START_REQUEST:
            return {...state, loading: true};
        case actionType.SUCCESS_REQUEST:
            return {...state, loading: false};
        case actionType.FAIL_REQUEST:
            return {...state, loading: false};

        // Get Data
        case actionType.GET_IMAM_DATA:
            return {...state, getData: action.imamData, suras: [...action.suras], isRandom: true};
        case actionType.GET_SERVER:
            return {...state, server: action.server};
        case actionType.SHOW_PLAYER:
            return {...state, showPlayer: true};
        case actionType.SURA_LINK:
            return {...state, suraLink: action.link};
        case actionType.SURA_LINK_RANDOM:
            return {...state, suraNumber: action.number, suraLink: `${state.server}/${action.number}.mp3`};

        // Favorite
        case actionType.PLAYLIST_STORAGE:
            return {...state, playlist: action.items}
        case actionType.ADD_TO_PLAYLIST:
            let allList = state.playlist && state.playlist.some(list => list.server === action.item.server);
            let item = null;
            if(action.item.isFavorite && !allList) {
                item = {...state, playlist: state.playlist.concat(action.item)};
            } else {
                item = {...state};
            }
            return item;
        case actionType.REMOVE_ITEM_FROM_PLAYLIST:
            let filterPlaylist = state.playlist.filter(list => list.server !== action.item.server);
            return {...state, playlist: filterPlaylist}
        case actionType.DELETE_FAVOURITE_FROM_SURABOX:
            return {...state, deleteFav: action.item}
        case actionType.EMPTY_FAVOURITE_FROM_SURABOX:
            return {...state, deleteFav: ''}
        case actionType.IS_FROM_FAVOURITE_PLAYLIST:
            return {...state, isFavPlaylist: action.booleanValue}
        case actionType.iS_FAVORITE_PAGE:
            return {...state, isFavoritePage: action.value}
        //  Theme
        case actionType.IS_THEME_IS_DARK:
            return {...state, isThemeDark: !state.isThemeDark, theme: state.isThemeDark ? 'light' : 'dark'}
        case actionType.THEME_STORAGE:
            return {...state, isThemeDark: action.value, theme: action.value ? 'dark' : 'light'}
        
        //  Language
        case actionType.CHOOSE_LANGUAGE:
            return {...state, language: action.selectLanguage}
        case actionType.SITE_LANGUAGE:
            return {...state, site_language: action.lang}

        default:
            return state;
    }
}
export default myReducer;
