import React from 'react';
import { BiPlay } from 'react-icons/bi';
import './playBtn.css';
import { connect } from 'react-redux';

const PlayBox = props => {
    const classes = ['play-btn', props.classes]
    return (
        <div className={classes.join(' ')} title={props.language.lang === 'ar' ? 'تشغيل' : 'play'} onClick={props.clicked}>
            <BiPlay size={props.size} color={props.color} />
        </div>
    )
};

const mapStateToProps = state => ({
    language: state.language
});

export default connect(mapStateToProps)(PlayBox);
