import React from 'react';
import Container from 'react-bootstrap/Container';
import { connect } from 'react-redux';
import AyatWhite from './ayatWhite.png';
import AyatBlack from './ayatBlack.png';
import { HeaderTitleStyled } from './headerTitleStyled';

const headerTitle = props => {
    return (
        <HeaderTitleStyled theme={props.theme} themeStyle={props.themeStyle}>
            
            {/* <Container className='h-100 d-flex align-items-center'>
                {
                    !props.isInMain
                    ? (
                        <div className='headerTitle_img'>
                            {
                                props.theme === 'light' ? <img src={AyatBlack} className='img-responsive' /> : <img src={AyatWhite} className='img-responsive' />
                            }
                        </div>
                    ) :
                    props.imamData && (
                        <div className='headerTitle_text'>
                            <h4 className='font-weight-bold mb-0'>{props.imamData.name}</h4>
                        </div>
                    )
                }
            </Container> */}
        </HeaderTitleStyled>
    )
}

const mapStateToProps = state => ({
    isInMain: state.isRandom,
    imamData: state.getData,
    theme: state.theme,
    themeStyle: state.themeStyle
});

export default connect(mapStateToProps)(headerTitle);
